mapper.delete:
delete
from gl_board
where brd_no = #{brdNo} and mbr_no = #{mbrNo}

mapper.select:
	select sub.*, count(bc.brd_no) commentCount
	from (
			select brd_no brdNo, mbr_no mbrNo, brd_title title, brd_content content, reg_dt regDate, chg_dt updateDate, brd_type type, brd_view viewCount
			from gl_board
			where brd_type = #{type}
		 ) sub left join gl_board_comment bc
    on sub.brdNo = bc.brd_no
    group by sub.brdNo
    order by sub.regDate desc
	limit #{startIndex}, #{perPageNum}

 	select brd_no brdNo, mbr_no mbrNo, brd_title title, brd_content content, reg_dt regDate, chg_dt updateDate, brd_type type, brd_view viewCount
	from gl_board
	where brd_no = #{brdNo}

select count(*)
from gl_board
where brd_type = #{type}
